  private def init(l: Language) = synchronized {
    lookup += l.terminology -> l
    lookup += l.bibliographic -> l
    l.iso639_1 match {
      case Some(v) => lookup += v.toLowerCase -> l
      case None => // Ignore
    }
    (l.englishNames ::: l.frenchNames ::: l.germanNames).foreach {
      case n => lookup += n.toLowerCase -> l
    }
  }

  override def get(name: String, caseSensitive: Boolean) = lookup.get(name.toLowerCase)
